import app from './node.js/server.js';
import mongodb from './node.js/mongodb.js';
import ReviewsDAO from './node.js/dao/reviewsDAO.js';

// This is the default port for the application
const MongoClient = mongodb.MongoClient;
const mongo_username = process.env.MONGO_USERNAME;
const mongo_password = process.env.MONGO_PASSWORD;

const url = `mongodb+srv://${mongo_username}:${mongo_password}@cluster0.2whti.mongodb.net/?
retryWrites=true&w=majority&appName=Cluster0`

const port = process.env.PORT || 8000;

MongoClient.connect(url, 
    {
        maxPoolSize: 50,
        wtimeoutMS: 2500,
        useNewUrlParser: true
    })
    .catch(err => {
        console.error(err.stack);
        process.exit(1);
    })
    .then(async client => {
        await ReviewsDAO.injectDB(client);
        app.listen(port, () => {
            console.log(`listening on port ${port}`);
        });
    }); 
